import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import {Link} from "react-router-dom";
import {Navigation} from "react-mdl";

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

export default function ButtonAppBar() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="Menu">
            <MenuIcon />
          </IconButton>
          <Typography variant="Button" className={classes.title}>
            News
          </Typography>
          <Navigation>
            <Link to={"/"}><Button style={{marginTop: '16px'}}>Home</Button></Link>
            <Link to="/projects"><Button style={{marginTop: '16px'}}>Projects</Button></Link>
            <a href="http://localhost:3000/resume.pdf" rel="noopener noreferrer" target="_blank"><Button style={{marginTop: '16px'}}>Resume</Button></a>
            <Link to="/aboutme"><Button style={{marginTop: '16px'}}>About Me</Button></Link>
            <Link to="/contact"><Button style={{marginTop: '16px'}}>Contact Me</Button></Link>
          </Navigation>

        </Toolbar>
      </AppBar>
    </div>
  );
}


